import React, {Component} from 'react';

class Name extends Component {
    constructor(props) {
        super(props);

        this.state = {
            fullName: {
                nameS: 'John',
                lname: 'Shepard'
            },

            contacts: {
                phoneNum: '123-456-789',
                emailAdd: 'totallyrealemail@legitservice.com'
            }

          }

        this.nameEditF = this.nameEditF.bind(this);
        this.nameSubmitF = this.nameSubmitF.bind(this);

        this.contactEditF = this.contactEditF.bind(this);
        this.contactSubmitF = this.contactSubmitF.bind(this);
        }

        nameEditF() {
          console.log("clicked fucker!");

          this.setState({
            fullName: {
                nameS: <NameForm nameSubmit= {this.nameSubmitF} namePlaceholder={this.state.fullName.nameS} lastPlaceholder={this.state.fullName.lname} />,
            }
          })
        }

        nameSubmitF() {
          console.log('Submitted!');

          this.setState({
            fullName: {
                nameS: document.querySelector('#nameInput').value,
                lname: document.querySelector('#lastInput').value
            }

          })
        }

        contactEditF(){
            console.log('edit contacts clicked!');

            this.setState({
                contacts: {
                    phoneNum: <ContactForm phonePlaceholder={this.state.contacts.phoneNum} emailPlaceholder= {this.state.contacts.emailAdd} contactSubmit={this.contactSubmitF}/>,
                    emailAdd: "editing"
                }

            })
          }

          contactSubmitF(){
            this.setState({
                contacts: {
                    phoneNum: document.querySelector('#phoneInput'),
                    emailAdd: document.querySelector('#emailInput')
                }
            })
          }

    render() {
        return (
            <div>
                <div className='nameDiv'>
                    {this.state.fullName.nameS} {this.state.fullName.lname}
                    </div>
                <div><button type='button' onClick={this.nameEditF}>Edit</button></div>

                <div className='contactDiv'>
                    <div>Contact Me:</div>
                    <div><button type = "button" onClick={this.contactEditF}>Edit</button></div>
                    <div>Phone: {this.state.contacts.phoneNum}</div>
                    <div>Email: {this.state.contacts.emailAdd}</div>
                </div>


            </div>
        )
    }
}

class NameForm extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <form>
                <label htmlFor = "nameInput">First Name:</label>
                <div><input type = "text" id='nameInput' defaultValue={this.props.namePlaceholder}></input></div>
                <label htmlFor = "lastInput">Last Name:</label>
                <div><input type = "text" id='lastInput' defaultValue={this.props.lastPlaceholder}></input></div>
                <button type='submit' onClick={this.props.nameSubmit}>Submit</button>
            </form>
        )
    }
}

class ContactForm extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <form>
                <label htmlFor='phoneInput'></label>
                <div><input type="number" id='phoneInput' defaultValue={this.props.phonePlaceholder}></input></div>
                <label htmlFor='emailInput'></label>
                <div><input type="email" id='emailInput' defaultValue={this.props.emailPlaceholder}></input></div>
                <button type='submit' onClick={this.props.contactSubmit}>Submit</button>
            </form>
        )
    }
}


export {Name, NameForm};
