import React, {Component} from 'react';
import './App.css';
import {Name} from './components/name';
import Summary from './components/summary';
import Education from './components/education';

class App extends Component {
  constructor(props) {
    super(props);

    this.previewBtnF = this.previewBtnF.bind(this);

  }

  previewBtnF () {
    console.log('previewed!')

    document.querySelectorAll('button').classList.add('hideBtn');
  }


  render() {
    return (
      <div>
        <div id='header'>CV Generator</div>
        <div id="mainWrapper">
          <div id='previewDiv'><button onClick = {this.previewBtnF}>Show Preview</button></div>
          <div id='leftWrapper'>
            <Name />
          </div>
          <div id='rightWrapper'>
            <Summary />
            <Education />
          </div>
        </div>
      </div>
    )
  }
}

//can use show preview button by hiding all buttons in css then undoing it again for editing purposes
export default App;
