import React, {Component} from 'react';
import './App.css';
import {Name} from './components/name';
import Summary from './components/summary';
import Education from './components/education';

class App extends Component {
  constructor(props) {
    super(props);

    this.previewBtnF = this.previewBtnF.bind(this);
    this.editBtnF = this.editBtnF.bind(this);

    this.state = {
      toggleButtons: <ShowBtn previewBtn = {this.previewBtnF} />,
    }
  }

  previewBtnF () {
    console.log('previewed!')

    const allButtons = document.querySelectorAll('.toggleVisual');

    for (const button of allButtons) {
      button.classList.add('hideBtn');
    }

    this.setState({
      toggleButtons: <EditBtn editBtn = {this.editBtnF} />
    })
  };

  editBtnF() {
    console.log('edited!')

    const allButtons = document.querySelectorAll('.toggleVisual');
    for (const button of allButtons) {
      button.classList.remove('hideBtn');
    }

    this.setState({
      toggleButtons: <ShowBtn previewBtn = {this.previewBtnF} />,
    })

  }





  render() {
    return (
      <div>
        <div id='header'>CV Generator</div>
        <div id="mainWrapper">
          <div id='previewDiv'>{this.state.toggleButtons}</div>

          <div id='leftWrapper'>
            <Name />
          </div>
          <div id='rightWrapper'>
            <Summary />
            <Education />
          </div>
        </div>
      </div>
    )
  }
}


class ShowBtn extends Component {
  constructor(props) {
    super(props);
  }

  render () {
    return (
      <button type='button' onClick={this.props.previewBtn}>Show Preview</button>
    )
  }
}

class EditBtn extends Component {
  constructor(props) {
    super(props);
  }

  render () {
    return (
      <button type='button' onClick={this.props.editBtn}>Edit Preview</button>
    )
  }
}

export default App;
