import React, {Component} from 'react';

class Name extends Component {
    constructor() {
        super();

        this.state = {
          fullName: 'Agent 47',
          newName: 'John Shepard',
        };
      }

      handleChange = (e) => {
        this.setState({
          fullName: e.target.value,
        });
      };

      onSubmitTask = (e) => {
        e.preventDefault();
        console.log("Submitted!");

        this.setState({
            newName: this.state.fullName,
        });
        console.log(this.state.fullName);


      };

      render() {

        return (
          <div>
            <form>
              <label htmlFor="taskInput">Enter Name:</label>
              <input
                onChange={this.handleChange}
                value={this.state.fullName}
                type="text"
                id="submitName"
              />
              <button type="submit" onClick={this.onSubmitTask}>Submit</button>
            </form>
            <Overview changedName={this.state.newName}/>
          </div>
        );
      }
}


class Overview extends Component {
    constructor(props){
        super(props);
    }


    render(){
        return(
            <div id='nameWrapper'>
              <div className="fullName">Badass: {this.props.changedName}</div>
              <div><button type='button' onClick={this.editClick}>Edit</button></div>
              </div>

        )
    }
}



export default Name;
