import React, {Component} from 'react';

class Name extends Component {
    constructor(props) {
        super(props);

        this.state = {
            fullName: {
                nameS: 'John',
                lname: 'Shepard'
            },

            contacts: {
                phoneNum: '123-456-789',
                emailAdd: 'totallyrealemail@foo.com'
            },

            hobbies: <DefaultHobbies />,

            listItem: [],    
          }
        this.nameEditF = this.nameEditF.bind(this);
        this.nameSubmitF = this.nameSubmitF.bind(this);

        this.contactEditF = this.contactEditF.bind(this);
        this.contactSubmitF = this.contactSubmitF.bind(this);

        this.interestEditF = this.interestEditF.bind(this);
        this.interestAddF = this.interestAddF.bind(this);
        this.clearInterestF = this.clearInterestF.bind(this);

        }

        nameEditF() {

          this.setState({
            fullName: {
                nameS: <NameForm  nameSubmit= {this.nameSubmitF} namePlaceholder={this.state.fullName.nameS} lastPlaceholder={this.state.fullName.lname} />,
            }
          })
          document.querySelector('#nameEditBtn').classList.add('hideBtn');
        }

        contactEditF(){
          console.log('Edit has been clicked!')

          this.setState({
            contacts: {
              phoneNum: <ContactForm contactSubmitF={this.contactSubmitF} contactPlaceholder={this.state.contacts.phoneNum} emailPlaceholder={this.state.contacts.emailAdd}/>
            }
          })
          document.querySelector('#contactEditBtn').classList.add('hideBtn'); 
        };

        interestEditF() {
          this.setState({
            hobbies:  <InterestForm interestAdd={this.interestAddF} clearInterest={this.clearInterestF}/>
          })
        }

        nameSubmitF(e) {
          e.preventDefault();
          console.log('Submitted!');

          this.setState({
            fullName: {
                nameS: document.querySelector('#nameInput').value,
                lname: document.querySelector('#lastInput').value
            }
          })
          document.querySelector('#nameEditBtn').classList.remove('hideBtn');
        };

        contactSubmitF(e) {
          e.preventDefault();
          console.log('Submitted');

          this.setState({
            contacts: {
              phoneNum: document.querySelector('#phoneInput').value,
              emailAdd: document.querySelector('#emailInput').value,
            }
          })
          document.querySelector('#contactEditBtn').classList.remove('hideBtn'); 
        }

        interestAddF(e) {
          e.preventDefault();
          console.log('interestAdded!');

          this.setState({
            hobbies: '',
            listItem: this.state.listItem.concat(document.querySelector('#interestInput').value),
          })

        }

        clearInterestF(deleteItem){
          const newItems = this.state.listItem.filter((item => item !== deleteItem));

          this.setState({
            listItem: newItems,
          })
          console.log(newItems);
        }

        render() {
            return (
            <div id='personDetailsWrapper'>
                <div className='nameDiv'>
                    <div className="fullName">{this.state.fullName.nameS} {this.state.fullName.lname}</div>
                    <div><button className='toggleVisual' id='nameEditBtn' type='button' onClick={this.nameEditF}>Edit</button></div>
                    </div>

              <div className='ContactsDiv'>
                <div className="contactWrapper">
                  <div className='contactHeader'>Contact:</div>
                  <button className='toggleVisual' type='button' id='contactEditBtn' onClick={this.contactEditF}>Edit</button>
                </div>
                <div>
                  <div>{this.state.contacts.phoneNum}</div>
                  <div>{this.state.contacts.emailAdd}</div>
                </div>
              </div>

              <div className='InterestDiv'>
                <div className="interestWrapper">
                  <div className='interestHeader'>Interests:</div>
                  <button type='button' className='toggleVisual' onClick={this.interestEditF}>Edit</button>
                </div>
                <div>{this.state.hobbies}</div>
                <ul>
                {this.state.listItem.map((item, i) => 
                <li id="interestItem" key={i}>{`${item} `}<button className='toggleVisual' type='button' onClick = {()=> this.clearInterestF(item)}>Delete</button></li> , )}
                </ul>
              </div>
            </div>
        )
    }
}

class NameForm extends Component {
    constructor(props) {
        super(props);
    }


    render() {
        return (
            <div className='nameForm'>
              <form>
                  <label htmlFor = "nameInput">First Name:</label>
                  <div><input type = "text" id='nameInput'  defaultValue={this.props.namePlaceholder} ></input></div>
                  <label htmlFor = "lastInput">Last Name:</label>
                  <div><input type = "text" id='lastInput' defaultValue={this.props.lastPlaceholder}></input></div>
                  <button type='submit' onClick={this.props.nameSubmit}>Submit</button>
              </form>
            </div>
        )
    }
}

class ContactForm extends Component {
  constructor(props) {
    super(props);
  }

  render(){
    return(
      <div>
        <form className='contactForm'>
          <label htmlFor = "phoneInput">Phone Number:</label>
                  <div><input type = "text" id='phoneInput'  defaultValue={this.props.contactPlaceholder} ></input></div>
                  <label htmlFor = "emailInput">Email Address:</label>
                  <div><input type = "email" id='emailInput' defaultValue={this.props.emailPlaceholder}></input></div>
                  <button type='submit' onClick={this.props.contactSubmitF}>Submit</button>
        </form>
      </div>
    )
  }

}

class InterestForm extends Component {
  constructor(props) {
    super(props);
  }

  render(){
    return(
      <div>
        <form>
          <label htmlFor='interestInput'></label>
          <div><input type='text' id='interestInput' placeholder='example: cooking'></input></div>
          <div id='interestAddBtn'><button  type='submit' onClick={this.props.interestAdd}>Add</button></div>

        </form>
      </div>
    )
  }
}

class DefaultHobbies extends Component {
  render(){
    return(
      <ul>
        <li>put</li>
        <li>your</li>
        <li>hobbies</li>
        <li>here</li>
      </ul>
    )
  }
}


export {Name};
