import React, {Component} from 'react';
import './App.css';
import {Name, SuperFinal} from './components/name';

class App extends Component {
  constructor(props) {
    super(props);

    this.state ={
      shownName: <SuperFinal superName="Set from app js!" />,
    }

    this.finalEdit = this.finalEdit.bind(this);
  }



  finalEdit() {
    this.setState({
      shownName: <Name onSubmitTask={this.onSubmitTask} />

    })
  }

  onSubmitTask = (e) => {
    e.preventDefault();
    // console.log("Submitted!");

    // this.setState({
    //     newName: this.state.fullName ,  

    // });
    this.setState({
      shownName: <SuperFinal superName= {document.querySelector('#submitName').value}/>
    })


    console.log('called from App');
  };

  render() {
    return (
      <div id = "mainWrapper">
        <div id="header">CV - Generator</div>
        <div>{this.state.shownName}</div>
        <button type='button' id='finalBtn' onClick={this.finalEdit}>Edit</button>



      </div>
    )
  }
}

export default App;
