import React, {Component} from 'react';

class Name extends Component {
    constructor() {
        super();

        this.state = {
          fullName: 'Agent 47',
          newName: '',

        };
      }

      handleChange = (e) => {
        this.setState({
          fullName: e.target.value,
        });
      };

      onSubmitTask = (e) => {
        e.preventDefault();
        console.log("Submitted!");

        this.setState({
            newName: this.state.fullName ,

            //need to add component with props here


        });
        console.log(this.state.fullName);
        // document.querySelector('#finalBtn').remove()

      };

      render() {

        return (

            <div>
              <form>
                <label htmlFor="taskInput">Enter Name:</label>
                <input
                  onChange={this.handleChange}
                  value={this.state.fullName} //we cannot change this
                  type="text"
                  id="submitName"
                />
                <button type="submit" onClick={this.onSubmitTask}>Submit</button>

                {/* <Overview changedName={this.state.newName}/> */}

              </form>
              <SuperFinal/>

            </div>
        );
      }
}


class Overview extends Component {
    constructor(props){
        super(props);

        this.state = {
          shownName: 'Overview Text',
        }

        this.editF = this.editF.bind(this);
    }

    editF() {
      console.log('edited!');

      this.setState({
        shownName: <Name shownValue={this.state.shownName}/>
      })

      document.querySelector('#editName').remove()

    }

    render(){
        return(
            <div id='nameWrapper'>
              <div className="fullName">{this.state.shownName}</div>
              <button type='button' id='editName' onClick={this.editF}>Edit</button>
              </div>

        )
    }
}


class Finalname extends Component {
  constructor(props){
    super(props);

    this.state = {
      finalText: 'this is the final class component: ',
    }

    this.finalEdit = this.finalEdit.bind(this);

  }

  finalEdit() {
    this.setState({
      finalText: <Name />

    })

    // document.querySelector('#finalBtn').remove()

  }

  render(){
    return(
      <div>
        {this.state.finalText}{this.props.printText}
        <button type='button' id='finalBtn' onClick={this.finalEdit}>Edit</button>

        </div>
    )
  }
}

class SuperFinal extends Component {
  constructor(props){
    super(props);
  }
  render(){
    return(
      <div>{this.props.superName}</div>
    )   
  }
}



export {Overview, Finalname, Name};
