import React, {Component} from 'react';

class Name extends Component {
    constructor() {
        super();

        this.state = {
          fullName: 'Agent 47',
        };
      }

      handleChange = (e) => {
        this.setState({
          fullName: e.target.value,
        });
      };

      onSubmitTask = (e) => {
        e.preventDefault();
        console.log("Submitted!");

        this.setState({
          fullName: document.querySelector('#submitName').value,
        });


        console.log(this.state.fullName);


      };

      render() {
        // const { task, tasks } = this.state;

        return (
          <div>
            <form>
              <label htmlFor="taskInput">Enter Name:</label>
              <input
                onChange={this.handleChange}
                value={this.state.fullName}
                type="text"
                id="submitName"
              />
              <button type="submit" onClick={this.onSubmitTask}>Submit</button>
            </form>
            <Overview changedName={this.state.fullName}/>
          </div>
        );
      }
}


class Overview extends Component {
    constructor(props){
        super(props);
    }
    render(){
        return(
            <div>Badass: {this.props.changedName}</div>
        )
    }
}



export default Name;
