import React, {Component} from 'react';

class Name extends Component {
    constructor(props) {
        super(props);

        this.state = {
            fullName: {
                nameS: 'John',
                lname: 'Shepard'
            },

            contacts: {
                phoneNum: '123-456-789',
                emailAdd: 'totallyrealemail@legitservice.com'
            }

          }

        this.nameEditF = this.nameEditF.bind(this);
        this.nameSubmitF = this.nameSubmitF.bind(this);
        }

        handleChangeF = (e) => {
            this.setState({
                fullName: {
                    nameS: e.target.value,
                }
            })
        }

        nameEditF() {
          console.log("clicked fucker!");

          this.setState({
            fullName: {
                nameS: <NameForm  nameSubmit= {this.nameSubmitF} namePlaceholder={this.state.fullName.nameS} lastPlaceholder={this.state.fullName.lname} handleChange={this.handleChangeF} />,
            }
          })
        }

        nameSubmitF() {
          console.log('Submitted!');

          this.setState({
            fullName: {
                nameS: document.querySelector('#nameInput').value,
                lname: document.querySelector('#lastInput').value
            }

          })
        }

        render() {
            return (
            <div>
                <div className='nameDiv'>
                    {/* {this.state.fullName.nameS} {this.state.fullName.lname} */}
                    *place name here
                    </div>
                <div><button type='button' onClick={this.nameEditF}>Edit</button></div>

                {/* <div className='contactDiv'>
                    <div>Contact Me:</div>
                    <div><button type = "button" onClick={this.contactEditF}>Edit</button></div>
                    <div>Phone: {this.state.contacts.phoneNum}</div>
                    <div>Email: {this.state.contacts.emailAdd}</div>
                </div> */}


            </div>
        )
    }
}

class NameForm extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <form>
                <label htmlFor = "nameInput">First Name:</label>
                <div><input onChange={this.props.handleChange} type = "text" id='nameInput'  value={this.props.namePlaceholder} ></input></div>
                <label htmlFor = "lastInput">Last Name:</label>
                <div><input type = "text" id='lastInput' defaultValue={this.props.lastPlaceholder}></input></div>
                <button type='submit' onClick={this.props.nameSubmit}>Submit</button>
            </form>
        )
    }
}

