import React, {Component} from 'react';

class Name extends Component {
    constructor() {
        super();

        this.state = {
          fullName: 'Agent 47',
        };
      }

      handleChange = (e) => {
        this.setState({
          task: {
            text: e.target.value,
          },
        });
      };

      onSubmitTask = (e) => {
        e.preventDefault();
        this.setState({
          fullName: <Overview changedName={document.querySelector('#submitName').value} />,
        });
      };

      render() {
        // const { task, tasks } = this.state;

        return (
          <div>
            <form>
              <label htmlFor="taskInput">Enter Name:</label>
              <input
                onChange={this.handleChange}
                value={this.state.task.text}
                type="text"
                id="submitName"
              />
              <button type="submit" onClick={this.onSubmitTask}>Submit</button>
            </form>
          </div>
        );
      }
}


class Overview extends Component {
    constructor(props){
        super(props);
    }

    render(){
        return(
            <div>{this.props.changedName}</div>
        )
    }
}



