import React, {Component} from "react";

class Education extends Component{
    constructor(props){
        super(props)

        this.addEduc = this.addEduc.bind(this);
        this.cancelFormF = this.cancelFormF.bind(this);
        this.saveFormF = this.saveFormF.bind(this);
        this.removeEducF = this.removeEducF.bind(this);

        this.state = {
            educRender: '',
            eduArray: [],
        }
    }

    addEduc(){
        console.log('add Educ!')

        this.setState({
            educRender: <EducForm saveForm={this.saveFormF} cancelForm={this.cancelFormF}  />
        })
    }

    cancelFormF() {
        this.setState({
            educRender: null,
        })
    }

    saveFormF(){
        console.log('saved education!');

        let totalValue = {
            school: document.querySelector('#schoolInput').value,
            date: document.querySelector('#edateInput').value,
            degree: document.querySelector('#degree').value,
            course: document.querySelector('#courseInput').value,
        };

        this.setState({
            educRender: '',
            eduArray: this.state.eduArray.concat(totalValue),
        })

        console.log(this.state.eduArray)
    }

    removeEducF(deleteItem) {
        console.log('deleted');

        const newEduc = this.state.eduArray.filter(currentItem => currentItem !== deleteItem);

        this.setState({
            eduArray: newEduc,
        })

    }
    render(){
        return(
            <div className="educDiv">
                <div id="educWrapper">
                    <div id="educHeader">Education</div>
                    <button className='toggleVisual' type="button" onClick={this.addEduc}>Add</button>
                </div>
                <div>{this.state.educRender}</div>
                <ul>
                    {this.state.eduArray.map((item, i) => <EducationList key={i} removeEduc={()=> this.removeEducF(item)} educItems = {Object.keys(item).map((key, i) => 
                     <div key={i}>{item[key]}</div>   )} />)}
                </ul>
            </div>
        )
    }
}

class EducForm extends Component{
    constructor(props){
        super(props)
    }
    render(){
        return(
            <form id="educForm">
                <label htmlFor="schoolInput">School:</label>
                <div><input type='text' id="schoolInput"></input></div>

                <label htmlFor="edateInput">Date Attended: (MM/YYYY - MM/YYYY)</label>
                <div><input type='text' id="edateInput"></input></div>

                <label htmlFor="degreeSelect">Degree:</label>
                <div>
                    <select id="degree">
                    <option value="none">None</option>
                    <option value="High School or equivalent">High School or equivalent</option>
                    <option value="Associate">Associate</option>
                    <option value="Bachelor's">Bachelor's</option>
                    <option value="Master's">Master's</option>
                    <option value="Doctorate">Doctorate</option>
                    </select>
                </div>

                <label htmlFor="courseInput">Course:</label>
                <div><input type='text' id='courseInput'></input></div>

                <div id="educBtns">
                    <button type="button" onClick={this.props.saveForm}>Save</button>
                    <button type="button" onClick={this.props.cancelForm}>Cancel</button>
                </div>
            </form>
        )
    }
}

class EducationList extends Component{
    constructor(props){
        super(props)
    }

    render(){
        return (
            <div id ='educContainer'>
            <div>{this.props.educItems}</div>
            <button className='toggleVisual' type="button" id="removeEducBtn" onClick={this.props.removeEduc}>Delete</button>
             </div>
        )
    }
}

export default Education;
