import React, {Component} from 'react';

class Name extends Component {
    constructor() {
        super();

        this.state = {
          fullName: 'Agent 47',
          newName: 'John Shepard',
        };
      }

      handleChange = (e) => {
        this.setState({
          fullName: e.target.value,
        });
      };

      onSubmitTask = (e) => {
        e.preventDefault();
        console.log("Submitted!");

        this.setState({
            newName:  this.state.fullName ,



        });
        console.log(this.state.fullName);


      };

      render() {

        return (

            <form>
              <label htmlFor="taskInput">Enter Name:</label>
              <input
                onChange={this.handleChange}
                value={this.props.shownValue}
                type="text"
                id="submitName"
              />
              <button type="submit" onClick={this.onSubmitTask}>Submit</button>

              {/* <Overview changedName={this.state.newName}/> */}
            </form>
        );
      }
}


class Overview extends Component {
    constructor(props){
        super(props);

        this.state = {
          shownName: 'Overview sample state',
        }

        this.editF = this.editF.bind(this);
    }

    editF() {
      console.log('edited!');

      this.setState({
        shownName: <Name shownValue={this.state.shownName}/>
      })

      document.querySelector('#editName').remove()

    }
// document.querySelector('button').remove()

    render(){
        return(
            <div id='nameWrapper'>
              <div className="fullName">{this.state.shownName}</div>
              <button type='button' id='editName' onClick={this.editF}>Edit</button>
              </div>

        )
    }
}



export default Overview;
