import React, {Component} from "react";

class Education extends Component{
    constructor(props){
        super(props)

        this.addEduc = this.addEduc.bind(this);
        this.cancelFormF = this.cancelFormF.bind(this);

        this.state = {
            educRender: '',
        }
    }

    addEduc(){
        console.log('add Educ!')

        this.setState({
            educRender: <EducForm cancelForm={this.cancelFormF} />
        })
    }

    cancelFormF() {
        this.setState({
            educRender: null,
        })
    }
    render(){
        return(
            <div className="educDiv">
                <div>Education</div>
                <button type="button" onClick={this.addEduc}>Add</button>
                <div>{this.state.educRender}</div>

            </div>
        )
    }
}

class EducForm extends Component{
    constructor(props){
        super(props)
    }

    render(){
        return(
            <form>
                <label htmlFor="schoolInput">School:</label>
                <div><input type='text' id="schoolInput"></input></div>

                <label htmlFor="edateInput">Date Attended: (MM/YYYY - MM/YYYY)</label>
                <div><input type='text' id="edateInput"></input></div>

                <label htmlFor="degreeSelect">Degree:</label>
                <div>
                    <select id="degree">
                    <option value="none">None</option>
                    <option value="High School or equivalent">High School or equivalent</option>
                    <option value="Associate">Associate</option>
                    <option value="Bachelor's">Bachelor's</option>
                    <option value="Master's">Master's</option>
                    <option value="Doctorate">Doctorate</option>
                    </select>
                </div>

                <label htmlFor="courseInput">Course:</label>
                <div><input type='text' id='courseInput'></input></div>

                <button type="button">Save</button>
                <button type="button" onClick={this.props.cancelForm}>Cancel</button>



            </form>
        )
    }
}

export default Education;
