import React, {Component} from 'react';

class Name extends Component {
    constructor(props) {
        super(props);

        this.state = {
            nameS: 'Altair State',
            lname: 'Last Name'
          }

        this.nameEditF = this.nameEditF.bind(this);
        this.nameSubmitF = this.nameSubmitF.bind(this);
        }

        nameEditF() {
          console.log("clicked fucker!");

          this.setState({
            nameS: <NameForm nameSubmit= {this.nameSubmitF} namePlaceholder={this.state.nameS} />
          })
        }

        nameSubmitF() {
          console.log('Submitted!');

          this.setState({
          nameS: document.querySelector('#nameInput').value
          })

    }

    render() {
        return (
            <div>
                <div className='nameDiv'>{this.state.nameS} {this.state.lname}</div>
                <div className='lastnameDiv'></div>
                <div><button type='button' onClick={this.nameEditF}>Edit</button></div>

            </div>
        )
    }
}

class NameForm extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <form>
                <label htmlFor = "nameInput">Name:</label>
                <input type = "text" id='nameInput' defaultValue={this.props.namePlaceholder}></input>
                <button type='submit' onClick={this.props.nameSubmit}>Submit</button>
            </form>
        )
    }
}


export {Name, NameForm};
