import React, {Component} from 'react';

class Summary extends Component{
    constructor(props){
        super(props);

        this.editSumF = this.editSumF.bind(this);
        this.submitSumF = this.submitSumF.bind(this);

        this.state = {
            sumText: 'Lorem Ipshit blah blah blah blah blah blah',
        }
    }

    editSumF(){
        console.log('Edited Summary!')

        this.setState({
            sumText: <SummaryForm submitSum={this.submitSumF}/>
        })
    }

    submitSumF(){
        console.log('submitted')

        this.setState({
            sumText: document.querySelector('#newSummary').value,
        })
    }

    render(){
        return(
            <div>
                <div className='sumDiv'>
                    <div>Summary:</div>
                    <button type='button' onClick={this.editSumF}>Edit</button>
                    <div>{this.state.sumText}</div>
                </div>

            </div>
        )
    }
}

class SummaryForm extends Component{
    constructor(props){
        super(props);
    }

    render(){
        return(
            <form>
                <textarea id= 'newSummary' cols={30} rows={10} placeholder={this.props.sumPlaceholder}></textarea>
                <button type='button' onClick={this.props.submitSum}>Submit</button>
            </form>
        )
    }
}

export default Summary;
