import React, {Component} from 'react';

class Name extends Component {
    constructor(props) {
        super(props);

        this.state = {
            fullName: {
                nameS: 'JohnFull',
                lname: 'ShepardFull'
            } 
          }

        this.nameEditF = this.nameEditF.bind(this);
        this.nameSubmitF = this.nameSubmitF.bind(this);
        }

        nameEditF() {
          console.log("clicked fucker!");

          this.setState({
            fullName: <NameForm nameSubmit= {this.nameSubmitF} namePlaceholder={this.state.nameS} />,
            // lname: <NameForm nameSubmit= {this.nameSubmitF} lastPlaceholder={this.state.lname}/>
          })
        }

        nameSubmitF() {
          console.log('Submitted!');

          this.setState({
          nameS: document.querySelector('#nameInput').value
          })

    }

    render() {
        return (
            <div>
                <div className='nameDiv'>{this.state.fullName.nameS} {this.state.fullName.lname}</div>
                <div><button type='button' onClick={this.nameEditF}>Edit</button></div>

            </div>
        )
    }
}

class NameForm extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        return (
            <form>
                <label htmlFor = "nameInput">First Name:</label>
                <div><input type = "text" id='nameInput' defaultValue={this.props.namePlaceholder}></input></div>
                <label htmlFor = "lastInput">Last Name:</label>
                <div><input type = "text" id='lastInput' defaultValue={this.props.lastPlaceholder}></input></div>
                <button type='submit' onClick={this.props.nameSubmit}>Submit</button>
            </form>
        )
    }
}


export {Name, NameForm};
