import React, {Component} from 'react';
import './App.css';
import {Name} from './components/name';
import Summary from './components/summary';
import Education from './components/education';

class App extends Component {
  constructor(props) {
    super(props);

    this.previewBtnF = this.previewBtnF.bind(this);

  }

  previewBtnF () {
    console.log('previewed!')

    const allButtons = document.querySelectorAll('.toggleVisual');

    for (const button of allButtons) {
      button.classList.add('hideBtn');
    }
  }


  render() {
    return (
      <div>
        <div id='header'>CV Generator</div>
        <div id="mainWrapper">
          <div id='previewDiv'><button id='previewBtn' onClick = {this.previewBtnF}>Show Preview</button></div>
          <div id='leftWrapper'>
            <Name />
          </div>
          <div id='rightWrapper'>
            <Summary />
            <Education />
          </div>
        </div>
      </div>
    )
  }
}


class ShowBtn extends Component {
  constructor(props) {
    super(props);
  }

  render () {
    return (
      <button type='button'>Show Preview</button>
    )
  }
}

export default App;
